# -*- coding: utf-8 -*- # svn 工具# author: kongyanan# date : 2016-11-03import osimport sysimport reimport subprocess# svn路径(测试使用)svnPath = "/Users/koba/Documents/work/FishingData"# 执行系统命令def execSys(cmd):	p = subprocess.Popen(cmd, shell=True, stdout=subprocess.PIPE, stderr=subprocess.STDOUT)	# for line in p.stdout.readlines():	#     print line,	retval = p.wait()	return retval,p.stdout.read()# 获取最新版本号def pull(pSvnPath):	cmd_svninfo = "cd "+pSvnPath+";git pull"	ret = execSys(cmd_svninfo)	if ret[0] == 0:		return ret[1]def commit(pSvnPath):	cmd_svninfo = "cd "+pSvnPath+";git add ./.;git commit -m\"robot commit\";git push"	ret = execSys(cmd_svninfo)	if ret[0] == 0:		return ret[1]if __name__ == '__main__':	pull(svnPath)